import "./dropdown.css";
import address from "../../assets/address.png";
import cross from "../../assets/cross.svg";
import React, { useState } from "react";
import cover from "../../assets/staking-bg.png";
import becomepepbutton from "../../assets/becomepepbutton.png";
import Navigation from "../../components/Navigation/index";

import WalletBar from "../../components/WalletBar/index";
import pokeball from "../../assets/pokeball-temp.png";
import dropdownarrow from "../../assets/dropdownarrow.png";
import pepemandercard from "../../assets/pepemandercard.png";
import uparrow from "../../assets/uparrow.png";

import subscribetoclaim from "../../assets/subscribetoclaim.png";
import subscribeandstake from "../../assets/subscribeandstake.png";
import unsubscribe from "../../assets/unsubscribe.png";
import approvelp from "../../assets/approvelp.png";

import provideliquiditybutton from "../../assets/provideliquiditybutton.png";
import ibutton from "../../assets/i.svg";
import enable from "../../assets/enable.png";
import { Button, Popover, PopoverHeader, PopoverBody } from "reactstrap";

function DropDown(props) {
  const [dorpdown, setDorpdown] = useState(false);

  const toggle = () => setDorpdown(!dorpdown);

  return (
    <div
      className={
        dorpdown ? "subscription-big-box" : "subscription-big-box-dropclose"
      }
    >
      <div
        className={
          dorpdown
            ? "subscription-box-big-header-drop"
            : "subscription-box-big-header"
        }
        onClick={toggle}
      >
        <div class="header-sub-div">
          <img
            src={pokeball}
            alt="logo"
            style={{ width: "50px", height: "50px" }}
          />

          <span class="header-text">{props.heading}</span>
        </div>
        {dorpdown ? (
          <div class="header-sub-div">
            <span class="Show-more">Show less</span>
            <img
              src={uparrow}
              alt="logo"
              style={{
                width: "13px",
                height: "13px",
                alignSelf: "center",
              }}
            />
          </div>
        ) : (
          <div class="header-sub-div">
            <span class="Show-more">Show more</span>
            <img
              src={dropdownarrow}
              alt="logo"
              style={{
                width: "13px",
                height: "13px",
                alignSelf: "center",
              }}
            />
          </div>
        )}
      </div>
      {dorpdown && (
        <div class="subscription-box-big-body">
          <div className="leftside-body">
            <span class="data-div-bold-text" style={{ fontSize: "17px" }}>
              Get Exclusive NFTs! Provide 100 PPDEX (+ETH) on Uniswap LP, stake
              these LP tokens and recieve a unique NFT every month. Your LP
              tokens will be locked for a minimum 32 days.
            </span>
            <br />
            <br />
            <span class="data-div-bold-text">PPBLZ-ETH LP balance</span>
            <span class="Buy-PPBLZ">Buy PPBLZ-ETH LP</span>

            <br />
            <br />
            <span className="number">4.00</span>
            <br />
            <br />
            <span class="data-div-bold-text">PPBLZ-ETH LP staked</span>
            <br />
            <br />
            <span className="number">0.00</span>
            <br />
            <br />

            <span class="data-div-bold-text" style={{ fontSize: "16px" }}>
              3.828 PPBLZ-ETH LP needed to subscribe
            </span>

{/* 
            <img src={subscribeandstake} style={{ width: "58%", marginTop: "10px" }} /> */}

            {/* <img src={approvelp} style={{ width: "58%", marginTop: "10px" }} /> */}
            <br />
            <br />

            <div class="Youhaveanactivesubscription">You have an active subscription</div>

            <img src={unsubscribe} style={{ width: "28%", marginTop: "10px" }} />

          </div>

          <div className="rightside-body">
            <div class="Rectangle"></div>

            <div style={{ width: "51%" }}>
              <span class="This-months-card">THIS MONTHS CARD:</span>
              <br />
              <br />
              <span class="Yugipepe">Yugipepe</span> <br />
              <span
                class="data-div-bold-text"
                style={{ fontSize: "16px", marginLeft: "0px" }}
              >
                Super cool discription about this card. Iuis aute irure dolor in
                reprehenderit in.
              </span>
              <div
                style={{
                  display: "flex",
                  width: "253px",
                  height: "356px",
                  objectFit: "cover",
                  backgroundSize: "cover",
                  backgroundRepeat: "no-repeat",
                  backgroundImage: `url(${pepemandercard})`,
                  marginTop:"14px"
                }}
              ></div>
              <img src={subscribetoclaim} style={{ width: "99%",  marginTop:"25px" }} />
            </div>
          </div>
        </div>
      )}{" "}
      {/* <div class="subscription-box-big-body">
      <div style={{ marginTop: "30px" }}>
        <span class="number" style={{ marginLeft: "0" }}>
          0 PPDEX
        </span>
      </div>

      <div style={{ display: "flex" }}>
        <p class="data-div-bold-text-big">Total value: $0</p>
        <p class="Update">Update</p>
      </div>

      <div style={{ marginTop: "6px" }}>
        <img src={provideliquiditybutton} className="enable" />
      </div>
    </div>

 */}
    </div>
  );
}

export default DropDown;
